package attestation

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestMainNitroVerifier(t *testing.T) {
	payload := `{
		"version": "v0.0.1",
		"attestation": {
			"format": "awsnitro",
			"body": ""
		}
	}`

	attestation, err := ParseAttestation(payload)
	assert.Nil(t, err)
	assert.Equal(t, 3, len(attestation.Measurements))
}
